mapper.select:
select count(*)
from map
inner join member
on map.member_id = member.id
where member.id = #{member_id}
and map.place_name = #{place_name}
and map.lat = #{lat}
and map.lon = #{lon}

mapper.insert:
insert into
map (
	place_name
	, address
	, lat
	, lon
	, member_id
) values (
	#{place_name}
	, #{address}
	, #{lat}
	, #{lon}
	, #{member_id}
)

mapper.delete:
delete from map
where member_id = #{member_id}
and place_name = #{place_name}
and address = #{address}
