mapper.select:
select *
from map
inner join member
on map.member_id = member.id


		and member.id = #{member_id}


		and map.address = #{address}

mapper.insert:
insert into
map (
	place_name
	, address
	, lat
	, lon
	, favo
	, member_id
) values (
	#{place_name}
	, #{address}
	, #{lat}
	, #{lon}
	, #{favo}
	, #{member_id}
)

mapper.delete:
delete from map


		and member_id = #{member_id}


		and address = #{address}
