mapper.select:
select *
from review
inner join member
on review.member_id = member.id
where member.id = #{member_id}
and place_name = #{place_name}
and address = #{address}
order by review.reg_date desc;

mapper.insert:
insert into
review (
	member_id
	,rating
	, content
	, nickname
	, reg_date
	, place_name
	, address
) values (
	#{member_id}
	, #{rating}
	, #{content}
	, #{nickname}
	, now()
	, #{place_name}
	, #{address}
)
